function FactList({ facts, setFacts }) {
  if (facts.length === 0)
    return (
      <p className="message messageNoFacts">
        No facts found for this category yet! Create the first one
      </p>
    );

  return (
    <section>
      <ul className="facts-list">
        {facts.map((fact) => (
          <Fact key={fact.id} fact={fact} setFacts={setFacts} />
        ))}
      </ul>
