}

function Fact({ fact, setFacts }) {
  const [isUpdating, setIsUpdating] = useState(false);
  const [userVote, setUserVote] = useState(null);
  const isDisputed =
    fact.votesInteresting + fact.votesMindblowing < fact.votesFalse;

  async function handleVote(columnName) {
    setIsUpdating(true);
    let updateObject = {};

    if (userVote === columnName) {
      // Remove the vote
      updateObject[columnName] = fact[columnName] - 1;
      setUserVote(null);
    } else {
      // Add new vote
      updateObject[columnName] = fact[columnName] + 1;

      // Remove previous vote if exists
      if (userVote) {
        updateObject[userVote] = fact[userVote] - 1;
      }

      setUserVote(columnName);
    }

    const { data: updatedFact, error } = await supabase
      .from("facts")
      .update(updateObject)
      .eq("id", fact.id)
      .select();

    setIsUpdating(false);
    if (!error) {
      setFacts((facts) =>
        facts.map((f) => (f.id === fact.id ? updatedFact[0] : f))
      );
    }
  }

  return (
    <li className="fact">
      <p>
        {isDisputed ? <span className="disputed"> [DISPUTED]</span> : null}
        {fact.text}
        <a
          className="source"
          href={fact.source}
          target="_blank"
          rel="noreferrer noopener"
        >
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{
          backgroundColor: CATEGORIES.find((cat) => cat.name === fact.category)
            .color,
        }}
      >
        {fact.category}
      </span>
      <div className="vote-buttons">
        <button
          onClick={() => handleVote("votesInteresting")}
          disabled={isUpdating}
          className={userVote === "votesInteresting" ? "voted" : ""}
        >
          üëç {fact.votesInteresting}
        </button>
        <button
          onClick={() => handleVote("votesMindblowing")}
          disabled={isUpdating}
          className={userVote === "votesMindblowing" ? "voted" : ""}
        >
          ü§Ø {fact.votesMindblowing}
        </button>
        <button
          onClick={() => handleVote("votesFalse")}
          disabled={isUpdating}
          className={userVote === "votesFalse" ? "voted" : ""}
        >
          ‚õî {fact.votesFalse}
        </button>
      </div>
    </li>
  );
}

function Pagination({ factsPerPage, totalFacts, paginate, currentPage }) {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalFacts / factsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="pagination-container">
      <span className="pagination-label">Pages</span>
      <nav>
        <ul className="pagination">
          {pageNumbers.map((number) => (
            <li key={number} className="page-item">
              <button
                onClick={() => paginate(number)}
                className={`page-link ${
                  currentPage === number ? "active" : ""
                }`}
              >
                {number}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

